from IPython.display import display, HTML

def format_message(role, content, color="#f0f0f0"):
    """Format a message with role styling"""
    role_colors = {
        "USER": "#e3f2fd",
        "ASSISTANT": "#e1f5fe"
    }
    bg_color = role_colors.get(role, color)

    html = f"""
    &lt;div style="margin: 20px 0; padding: 15px; background-color: {bg_color}; 
                border-radius: 10px;                 border-left: 4px solid {'#1976d2' if role == 'USER' else '#0288d1'};"&gt;
        &lt;h3 style="margin-top: 0; color: {'#1565c0' if role == 'USER' else '#01579b'};"&gt;
            {role}
        &lt;/h3&gt;
        &lt;div style="line-height: 1.6; color: #333;"&gt;
            {content}
        &lt;/div&gt;
    &lt;/div&gt;
    """
    return HTML(html)

